import { Fragment } from "react";
import { NavLink } from "react-router-dom";
import { useContext } from "react";
import AuthContext from "../../store/auth-context";

import HeaderCartButton from "./HeaderCartButton";
import surfImage from "../../assets/surfing.jpg";
import style from "./Header.module.css";

const CartHeader = (props) => {
  const authCtx = useContext(AuthContext);
  const isLoggedIn = authCtx.inLoggedIn;

  const logoutHandler = () => {
    authCtx.logout();
  };

  return (
    <Fragment>
      <header className={style.header}>
        <nav>
          <ul>
            <li>
              <NavLink className={style.active} to="/HomePage">
                Surf Shop
              </NavLink>
            </li>
            {isLoggedIn && (
              <li>
                <NavLink className={style.active} to="/login">
                  Log in
                </NavLink>
              </li>
            )}
          </ul>
        </nav>
        <HeaderCartButton onClick={props.onShowCart} />
      </header>
      <div className={style["main-image"]}>
        <img src={surfImage} alt="A table full of delicious food!" />
      </div>
    </Fragment>
  );
};

export default CartHeader;
