import React, { useContext, useState } from "react";
//import { useNavigate } from "react-router-dom";
import CartItem from "../components/Cart/CartItem";
import classes from "../components/Cart/Cart.module.css";
import CartContext from "../store/cart-context";

//import AuthContext from "../store/auth-context";
// import CheckoutFunctional from "../components/Checkout/Checkoutfunctional";
import CheckoutFunctional from "../components/Checkout/Checkoutfunctional";

const CheckoutPage = () => {
  //  const navigate = useNavigate;
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [didSubmit, setDidSubmit] = useState(false);

  const cartCtx = useContext(CartContext);

  let products = JSON.parse(localStorage.getItem("items"));
  let userName = localStorage.getItem("username");
  userName = userName.replace(".", "");
  let url = `https://capstone-1-334cc-default-rtdb.europe-west1.firebasedatabase.app/orders/${userName}.json`;

  let totalAmountLocal = JSON.parse(localStorage.getItem("totalAmountLocal"));
  //set local storage to 0, otherwise nothing works
  if (totalAmountLocal === null) {
    totalAmountLocal = 0;
  }
  //change this so we still use cartCtx.totalAmount but change cartCtx to the local storage
  const totalAmountExTax = `£${totalAmountLocal.toFixed(2)}`;
  const tax = `£${(totalAmountLocal * 0.1).toFixed(2)}`;
  const totalAmountIncTax = `£${(totalAmountLocal * 1.1).toFixed(2)}`;

  const hasItems = cartCtx.items.length > 0;

  const cartItemRemoveHandler = (id) => {
    cartCtx.removeItem(id);
  };

  const cartItemAddHandler = (item) => {
    cartCtx.addItem({ ...item, amount: 1 });
  };

  const submitOrderHandler = async (userData) => {
    setIsSubmitting(true);
    const response = await fetch(url, {
      method: "POST",
      body: JSON.stringify({
        user: userData,
        orderedItems: cartCtx.items,
      }),
    });
    setIsSubmitting(false);
    setDidSubmit(true);
    cartCtx.clearCart();
  };

  // doesnt work
  //navigate("/profile");
  // className="w-25 p-3"
  const cartItems = (
    <div className="col-md-5 col-lg-4 order-md-last">
      <ul className={classes["cart-items"]}>
        {products.map((item) => (
          <CartItem
            key={item.id}
            name={item.name}
            amount={item.amount}
            price={item.price}
            image={item.image}
            onRemove={cartItemRemoveHandler.bind(null, item.id)}
            onAdd={cartItemAddHandler.bind(null, item)}
          />
        ))}
      </ul>
    </div>
  );
  //make a seperate div for the cart here, use css to style to the left
  return (
    <React.Fragment>
      <div className="text-center p-5">
        <h1 className="p-1 ml-1"> Hello {userName}, your current cart:</h1>
        <div className="row g-5 p2 ml-1">
          {cartItems}
          <div className={classes.total}>
            <span>Total ex Tax</span>
            <span>{totalAmountExTax}</span>
          </div>
          <span>Tax (10%) </span>
          <span>{tax}</span>
          <div className={classes.total}>
            <span>Total Amount</span>
            <span>{totalAmountIncTax}</span>
          </div>
          <div>
            <CheckoutFunctional onConfirm={submitOrderHandler} />
          </div>
        </div>
      </div>
    </React.Fragment>
  );
};

export default CheckoutPage;
