import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import { useEffect, useState } from "react";

const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  useEffect(
    /*async*/ () => {
      //creating a new function to do the fetch means you can use async, async cannot be used directly on useEffect like above
      const fetchMeals = async () => {
        const response = await fetch(
          "https://intersurf-cb425-default-rtdb.europe-west1.firebasedatabase.app/meals.json"
        );
        const responseData = await response.json();
        //doing this creates an array from the object we recieve in data
        const loadedMeals = [];
        for (const key in responseData) {
          loadedMeals.push({
            id: key,
            name: responseData[key].name,
            description: responseData[key].description,
            price: responseData[key].price,
          });
        }

        setMeals(loadedMeals);
        setIsLoading(false);
      };
      fetchMeals();
    },
    []
  );

  if (isLoading) {
    return (
      <section className={classes.MealsLoading}>
        <p> Loading... </p>
      </section>
    );
  }

  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
