import { Fragment } from "react";
import { NavLink, useParams } from "react-router-dom";
import { useContext } from "react";
import AuthContext from "../../store/auth-context";

import HeaderCartButton from "./HeaderCartButton";
import shirtHeaderImg from "../../assets/test.png";
//import other imgs for each page header image
import style from "./Header.module.css";

const CartHeader = (props) => {
  let { id } = useParams();
  const authCtx = useContext(AuthContext);
  const isLoggedIn = authCtx.isLoggedIn;

  let url = "";
  console.log((url = window.location.href));
  console.log(url.slice(-1, 4));

  const logoutHandler = () => {
    authCtx.logout();
    localStorage.removeItem("username");
  };
  //fix use params lecture 272
  if (id === "HomePage") {
    console.log("home");
  }
  return (
    <Fragment>
      <header className={style.header}>
        <nav>
          <ul>
            <li>
              <NavLink
                className={(navData) => (navData.isActive ? style.active : "")}
                to="/shirts"
              >
                Shirts
              </NavLink>
            </li>
            <li>
              <NavLink
                className={(navData) => (navData.isActive ? style.active : "")}
                to="/Books"
              >
                Books
              </NavLink>
            </li>
            {!isLoggedIn && (
              <li>
                <NavLink
                  className={(navData) =>
                    navData.isActive ? style.active : ""
                  }
                  to="/auth"
                >
                  Log in
                </NavLink>
              </li>
            )}
            {isLoggedIn && (
              <li>
                <NavLink
                  className={(navData) =>
                    navData.isActive ? style.active : ""
                  }
                  to="/profile"
                >
                  Profile
                </NavLink>
              </li>
            )}
            {isLoggedIn && (
              <li>
                <NavLink
                  onClick={logoutHandler}
                  className={(navData) =>
                    navData.isActive ? style.active : ""
                  }
                  to="/logout"
                >
                  Logout
                </NavLink>
              </li>
            )}
          </ul>
        </nav>
        <HeaderCartButton onClick={props.onShowCart} />
      </header>
      <div className={style["main-image"]}>
        {}
        <img src={shirtHeaderImg} alt="A table full of delicious food!" />
      </div>
    </Fragment>
  );
};

export default CartHeader;
