import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import InputGroup from "react-bootstrap/InputGroup";
import Row from "react-bootstrap/Row";

import { useRef, useState } from "react";

const isEmpty = (value) => value.trim() === "";
const isValidPost = (value) => value.trim().length !== 5;

const CheckoutFunctionalBootstrap = (props) => {
  const [validated, setValidated] = useState(false);

  const submitHandler = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    setValidated(true);
    if (form.checkValidity() === true) {
      event.preventDefault();
      event.stopPropagation();
      let name =
        event.currentTarget[0].value + " " + event.currentTarget[1].value;
      let street =
        event.currentTarget[2].value + " " + event.currentTarget[3].value;
      let city =
        event.currentTarget[4].value + " " + event.currentTarget[5].value;
      let postalCode = event.currentTarget[6].value;
      let ccName = event.currentTarget[7].value;
      let ccNumber = event.currentTarget[8].value;
      let ccExpiration = event.currentTarget[9].value;
      let ccCVV = event.currentTarget[11].value;
      let username = localStorage.getItem("username");
    }
    // props.onConfirm({
    //   name: enteredName,
    //   street: enteredStreet,
    //   city: enteredCity,
    //   postalCode: enteredPostalCode,
    //   username: userNameRef,
    // });
  };

  return (
    <Form noValidate validated={validated} onSubmit={submitHandler}>
      <Row className="mb-3">
        <Form.Group as={Col} md="4" controlId="validationCustom01">
          <Form.Label>First name</Form.Label>
          <Form.Control required type="text" placeholder="First name" />
          <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        </Form.Group>
        <Form.Group as={Col} md="4" controlId="validationCustom02">
          <Form.Label>Last name</Form.Label>
          <Form.Control required type="text" placeholder="Last name" />
          <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        </Form.Group>
      </Row>
      <Row className="mb-3">
        <Form.Group as={Col} md="6" controlId="validationCustom03">
          <Form.Label>Street</Form.Label>
          <Form.Control required type="text" placeholder="Address" />
          <Form.Control.Feedback type="invalid">
            Please provide a valid Address.
          </Form.Control.Feedback>
          <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        </Form.Group>
        <Form.Group as={Col} md="6" controlId="validationCustom04">
          <Form.Label>House Number</Form.Label>
          <Form.Control required type="text" placeholder="House Number/Name" />
          <Form.Control.Feedback type="invalid">
            Please provide a valid Number/Name.
          </Form.Control.Feedback>
          <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        </Form.Group>
      </Row>
      <Row className="mb-3">
        <Form.Group as={Col} md="6" controlId="validationCustom05">
          <Form.Label>City</Form.Label>
          <Form.Control type="text" placeholder="City" required />
          <Form.Control.Feedback type="invalid">
            Please provide a valid city.
          </Form.Control.Feedback>
        </Form.Group>
        <Form.Group as={Col} md="3" controlId="validationCustom06">
          <Form.Label>State</Form.Label>
          <Form.Control type="text" placeholder="State" required />
          <Form.Control.Feedback type="invalid">
            Please provide a valid state.
          </Form.Control.Feedback>
        </Form.Group>
        <Form.Group as={Col} md="3" controlId="validationCustom07">
          <Form.Label>Zip</Form.Label>
          <Form.Control
            type="text"
            placeholder="Zip"
            required
            minLength={3}
            maxLength={15}
          />
          <Form.Control.Feedback type="invalid">
            Please provide a valid zip.
          </Form.Control.Feedback>
        </Form.Group>
      </Row>

      <Row className="mb-3">
        <Form.Group as={Col} md="6" controlId="validationCustom08">
          <Form.Label>Name on card</Form.Label>
          <Form.Control
            required
            type="text"
            placeholder="John Smith"
            minLength={5}
          />
          <Form.Control.Feedback type="invalid">
            Please provide name.
          </Form.Control.Feedback>
          <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        </Form.Group>
        <Form.Group as={Col} md="6" controlId="validationCustom09">
          <Form.Label>Card Number</Form.Label>
          <Form.Control
            required
            type="text"
            placeholder="**** **** **** 1234"
            minLength={16}
            maxLength={16}
          />
          <Form.Control.Feedback type="invalid">
            Please provide a Card number.
          </Form.Control.Feedback>
          <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        </Form.Group>
      </Row>

      <Row className="mb-3">
        <Form.Group as={Col} md="6" controlId="validationCustom10">
          <Form.Label>Expiration</Form.Label>
          <Form.Control
            required
            type="text"
            placeholder="mm/yy"
            minLength={5}
            maxLength={5}
          />
          <Form.Control.Feedback type="invalid">
            Please provide a month/year format.
          </Form.Control.Feedback>
          <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        </Form.Group>
        <Form.Group as={Col} md="6" controlId="validationCustom11">
          <Form.Label>CVV</Form.Label>
          <Form.Control
            required
            type="text"
            placeholder="***"
            minLength={3}
            maxLength={3}
          />
          <Form.Control.Feedback type="invalid">
            Please provide a CVV.
          </Form.Control.Feedback>
          <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        </Form.Group>
      </Row>
      <Form.Group className="mb-3">
        <Form.Check
          label="Create username using lastname and password using city?"
          feedback="You must agree before submitting."
          feedbackType="invalid"
        />
      </Form.Group>
      <Button type="submit">Confirm Order</Button>
    </Form>
  );
};

export default CheckoutFunctionalBootstrap;
