import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import { useEffect, useState } from "react";

const AvailableBooks = () => {
  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState(null);

  useEffect(
    /*async*/ () => {
      //creating a new function to do the fetch means you can use async, async cannot be used directly on useEffect like above
      const fetchMeals = async () => {
        const response = await fetch(
          "https://capstone-1-334cc-default-rtdb.europe-west1.firebasedatabase.app/shirts.json"
        );
        //if the response is not okay
        if (!response.ok) {
          throw new Error("Something went wrong!");
        }

        const responseData = await response.json();
        //doing this creates an array from the object we recieve in data
        const loadedMeals = [];
        for (const key in responseData) {
          loadedMeals.push({
            id: key,
            name: responseData[key].name,
            image: responseData[key].image,
            price: responseData[key].price,
          });
        }

        setMeals(loadedMeals);
        setIsLoading(false);
      };
      //another good reason to put the fetch meals func inside the other func is
      //we can do this catch for errors

      fetchMeals().catch((error) => {
        setIsLoading(false);
        setHttpError(error.message);
      });
    },
    []
  );

  if (isLoading) {
    return (
      <section className={classes.MealsLoading}>
        <p> Loading... </p>
      </section>
    );
  }

  if (httpError) {
    return (
      <section className={classes.MealsError}>
        <p> {httpError} </p>
      </section>
    );
  }

  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      image={meal.image}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableBooks;
