import { useEffect, useState } from "react";
import OrderedItem from "./OrderedItem";
import styles from "ProfilePastOrders.module.css";

let userName = "";
let localUserName = localStorage.getItem("username");
if (localUserName) {
  userName = localUserName.replace(".", "");
}

const ProfilePastOrders = () => {
  const [orders, setOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      const response = await fetch(
        `https://capstone-1-334cc-default-rtdb.europe-west1.firebasedatabase.app/orders/${userName}.json`
      );
      //if response is not okay
      if (!response.ok) {
        throw new Error("something went wrong, have you ordered here before?");
      }

      const responseData = await response.json();
      //create array from response object
      const loadedOrders = [];
      for (let i in responseData) {
        loadedOrders.push({
          fetchedOrderedItems: responseData[i].orderedItems,
        });
      }
      console.log(loadedOrders);

      setOrders(loadedOrders);
      setIsLoading(false);
    };
    fetchOrders().catch((error) => {
      setIsLoading(false);
      setHttpError(error.message);
    });
  }, []);

  if (isLoading) {
    return (
      <section className={classes.OrdersLoading}>
        <p> Loading... </p>
      </section>
    );
  }
  if (httpError) {
    return (
      <section className={classes.OrdersError}>
        <p> {httpError} </p>
      </section>
    );
  }

  const ordersList = orders.map((order) => (
    <OrderedItem
      key={order.id}
      id={order.id}
      name={order.name}
      image={order.image}
      price={order.price}
    />
  ));
};

export default ProfilePastOrders;
